import { fetchPaymentIntent } from './services';
import { useSelector } from 'react-redux';
import {getWalletSelector} from '@state/Wallet/selectors';
import { getCurrency } from '@state/Preference/selector';
import { hashQueryKey, useQueries, useQuery } from '@tanstack/react-query';

export const paymentIntentKeys = {
  all: ['paymentIntents'] as const,
  paymentIntent: () => [...paymentIntentKeys.all, 'paymentIntent'] as const,
};

export const getFetchPaymentIntentQuery = (paymentIntentId: string) => ({
  queryKey: paymentIntentKeys.paymentIntent(),
  queryFn: () => fetchPaymentIntent(paymentIntentId),
});


export const useWalletsAccumulatedTotalBalanceQuery = () => {
  const { wallets } = useSelector(getWalletSelector());
  const currency = useSelector(getCurrency);
  const walletsList = Object.values(wallets);

  const computeAccumulatedBalance = () => {
    return walletsList.reduce((total, { address }) => {
      const walletBalance = queryClient.getQueryData([
        ...walletKeys.wallet(address),
        'total',
      ]);
      if (typeof walletBalance !== 'number') {
        return 0;
      }
      return total + walletBalance;
    }, 0);
  };

  return useQuery({
    queryKey: walletKeys.walletsAccumulatedTotalBalance(
      walletsList.map(wallet => wallet.address),
      currency,
    ),
    queryFn: computeAccumulatedBalance,
  });
};
