import { useState, useEffect } from "react";
import { Button, Stack, Typography, Box, Card, CardContent, TextField } from "@mui/material";
import { useAuth0 } from "@auth0/auth0-react";

function Login() {

  const [pending, setPending] = useState(true);
  useEffect(() => {

    const searchParams = new URLSearchParams(window.location.search);
    const idParam = searchParams.get('id');
    const returnUrlParam = searchParams.get('returnUrl');
    const amountParam = searchParams.get('amount');
    const descriptionParam = searchParams.get('description');
    if (amountParam) 
    {
      setPending(false);
      const url = process.env.REACT_APP_AUTH0_REDIRECT_URI+`/?id=${idParam}&amount=${amountParam}&description=${descriptionParam}&returnUrl=${returnUrlParam}`;
      localStorage.setItem('transactionUrl', url);
    }

  }, []);



  const { loginWithRedirect } = useAuth0();

  const handleLoginWithGoogle = async () => await loginWithRedirect();

  return (
    <Stack
      alignItems="center"
      sx={{
        "& .MuiTextField-root": {
          margin: 2,
          width: "300px",
        },
      }}
    >
      <Typography
        variant="h6"
        gutterBottom
        color="#6b7280"
        mt="1em"
        textAlign="center"
      >
        Connect your Pitaka account to sign in.
      </Typography>
      <Button
        variant="contained"
        onClick={handleLoginWithGoogle}
        size="large"
        sx={{
          width: "300px",
          margin: 4,
          padding: 1.5,
        }}
        disabled={false}
      >
        Single Sign-On (SSO)
      </Button>
    </Stack>
  );
}

export default Login;
